mapper.update:

UPDATE TSJX_EAP_ANSWER
   SET DELETED     = #{deleted,jdbcType=VARCHAR},
       MODIFY_BY   = #{modifyBy,jdbcType=VARCHAR},
       MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}
 WHERE ID = #{id,jdbcType=INTEGER}

mapper.select:


 FROM TSJX_EAP_ANSWER
WHERE ID = #{id,jdbcType=INTEGER}
  AND DELETED = #{deleted,jdbcType=VARCHAR}



  FROM TSJX_EAP_ANSWER



		AND ID = #{id,jdbcType=INTEGER}


		AND EVA_TYPE = #{evaType,jdbcType=VARCHAR}


		AND NUM = #{num,jdbcType=VARCHAR}


		AND DIMENSION = #{dimension,jdbcType=VARCHAR}


		AND OPTIONS = #{options,jdbcType=VARCHAR}


		AND SCORE = #{score,jdbcType=VARCHAR}


		AND SEX = #{sex,jdbcType=VARCHAR}


		AND DELETED = #{deleted,jdbcType=VARCHAR}
