mapper.update:

UPDATE EAP_EVALUATING
   SET DELETED     = #{deleted,jdbcType=VARCHAR},
       MODIFY_BY   = #{modifyBy,jdbcType=VARCHAR},
       MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}
 WHERE ID = #{id,jdbcType=INTEGER}

mapper.select:


 FROM EAP_EVALUATING
WHERE ID = #{id,jdbcType=INTEGER}
  AND DELETED = #{deleted,jdbcType=VARCHAR}



  FROM EAP_EVALUATING



		AND ID = #{id,jdbcType=INTEGER}


		AND USER_ID = #{userId,jdbcType=INTEGER}


		AND USER_NAME = #{userName,jdbcType=VARCHAR}


		AND USER_PHONE = #{userPhone,jdbcType=VARCHAR}


		AND COMPANY = #{company,jdbcType=VARCHAR}


		AND SEX = #{sex,jdbcType=VARCHAR}


		AND AGE = #{age,jdbcType=VARCHAR}


		AND EVA_TYPE = #{evaType,jdbcType=VARCHAR}


		AND EVA_NAME = #{evaName,jdbcType=VARCHAR}


		AND ANSWER = #{answer,jdbcType=VARCHAR}


		AND SCORE = #{score,jdbcType=VARCHAR}


		AND RESULT = #{result,jdbcType=VARCHAR}


		AND DELETED = #{deleted,jdbcType=VARCHAR}
