mapper.select:


 FROM TSJX_NOTICE
WHERE ID = #{id,jdbcType=INTEGER}
  AND DELETED = #{deleted,jdbcType=VARCHAR}



  FROM TSJX_NOTICE



		AND ID = #{id,jdbcType=INTEGER}


		AND USER_ID = #{userId,jdbcType=INTEGER}


		AND TITLE = #{title,jdbcType=VARCHAR}


		AND CONTENT = #{content,jdbcType=VARCHAR}


